mapper.update:
UPDATE lt_ad_facebook_report_ad t1, lt_facebook_ad t2, lt_ad_advertise_variation t3 set t1.markup_type = #{markupType}, t1.markup_amount = #{markupAmount}, t1.charge_amount = t1.cost + #{markupAmount}*t1.clicks,
t1.is_charged = #{isCharged},
t1.lt_ad_advertise_id = t3.ad_advertise_id where t1.ad_id = t2.id and t2.lt_ad_advertise_variation_id = t3.id and t1.date = #{date} and t3.company_id = #{companyId}

UPDATE lt_ad_facebook_report_ad t1, lt_facebook_ad t2, lt_ad_advertise_variation t3 set t1.markup_type = #{markupType}, t1.markup_amount = #{markupAmount}, t1.charge_amount = t1.cost * (1 + #{markupAmount}),
t1.is_charged = #{isCharged},
t1.lt_ad_advertise_id = t3.ad_advertise_id where t1.ad_id = t2.id and t2.lt_ad_advertise_variation_id = t3.id and t1.date = #{date} and t3.company_id = #{companyId}

mapper.select:
  	select sum(charge_amount) from lt_ad_facebook_report_ad t1
	where t1.lt_ad_advertise_id = #{ltAdAdvertiseId}
	and t1.date = #{date}
