mapper.update:
update lt_ad_google_adwords_report_automatic_placements t1, lt_google_adwords_ad_group t2, lt_ad_group t3 set t1.markup_type = #{markupType}, t1.markup_amount = #{markupAmount}, t1.charge_amount = t1.cost/1000000 + #{markupAmount}*t1.clicks,
t1.is_charged = #{isCharged},
t1.lt_ad_group_id = t2.lt_ad_group_id where t1.ad_group_id = t2.id and t2.lt_ad_group_id = t3.id and t1.date = #{date} and t3.company_id = #{companyId}

update lt_ad_google_adwords_report_automatic_placements t1, lt_google_adwords_ad_group t2, lt_ad_group t3 set t1.markup_type = #{markupType}, t1.markup_amount = #{markupAmount}, t1.charge_amount = t1.cost/1000000 * (1 + #{markupAmount}),
t1.is_charged = #{isCharged},
t1.lt_ad_group_id = t2.lt_ad_group_id where t1.ad_group_id = t2.id and t2.lt_ad_group_id = t3.id and t1.date = #{date} and t3.company_id = #{companyId}
