mapper.select:
SELECT t.id, t.ebay_listing_id, t.store_id, t.company_id, pc.`value` AS primarycate, sc.`value` AS secondarycate, getCategoryIdLevel1 (pc.`value`, t.site_id) AS primaryLevelTcate, getCategoryIdLevel1 (sc.`value`, t.site_id) AS secondaryLevelTcate, site.`value` AS site, ed.site_id, eet.`VALUE` AS listingDesc FROM lt_ebay_listing t LEFT JOIN lt_ebay_entity_varchar AS eev ON eev.ebay_entity_id = t.id AND eev.ebay_entity_attribute_id = 247 LEFT JOIN lt_ebay_entity_container eec ON eec.ebay_entity_id = t.id AND eec.ebay_entity_attribute_id = 336 LEFT JOIN lt_ebay_entity_varchar AS pc ON pc.ebay_entity_id = t.id AND pc.ebay_entity_attribute_id = 105 LEFT JOIN lt_ebay_entity_varchar AS sc ON sc.ebay_entity_id = t.id AND sc.ebay_entity_attribute_id = 140 LEFT JOIN lt_ebay_entity_varchar AS site ON site.ebay_entity_id = t.id AND site.ebay_entity_attribute_id = 322 LEFT JOIN lt_ebay_detail AS ed ON ed.site_name = site.`value` LEFT JOIN lt_ebay_entity_text AS eet ON eet.ebay_entity_id = t.id AND eet.ebay_entity_attribute_id = 23 WHERE eev.`VALUE` = 'Active' AND t.is_active = 1
AND t.company_id = #{currentCompanyId}
AND t.store_id = #{currentStoreId}
AND eec.id is null
AND eec.id is not null
AND site.`value` in
#{item}
limit #{start}, #{end}

SELECT count(t.id) FROM lt_ebay_listing t LEFT JOIN lt_ebay_entity_varchar AS eev ON eev.ebay_entity_id = t.id AND eev.ebay_entity_attribute_id = 247 LEFT JOIN lt_ebay_entity_container eec on eec.ebay_entity_id = t.id AND eec.ebay_entity_attribute_id = 336 WHERE eev.`VALUE` = 'Active' AND is_active = 1
AND t.company_id = #{currentCompanyId}
AND t.store_id = #{currentStoreId}
AND eec.id is null
AND eec.id is not null
AND site.`value` in
#{item}

SELECT ed.site_id as site_id,ed.`name` site_name FROM `lt_ebay_listing` `t` left join lt_ebay_entity_varchar as pc on pc.ebay_entity_id = t.id and pc.ebay_entity_attribute_id = 105 left join lt_ebay_entity_varchar as sc on sc.ebay_entity_id = t.id and sc.ebay_entity_attribute_id = 140 left join lt_ebay_entity_varchar as sstatus on sstatus.ebay_entity_id = t.id and sstatus.ebay_entity_attribute_id = 247 left join lt_ebay_entity_varchar as site on site.ebay_entity_id = t.id and site.ebay_entity_attribute_id = 322 inner join lt_ebay_detail ed on t.site_id=ed.site_id and site.value=ed.site_name where sstatus.value = 'Active' and t.company_id = #{companyId}
and t.store_id = #{storeId}
group by site_id,site_name
